@app.route("/_timer", methods=["GET", "POST"])
def timer():
    global sollar_sys
    pos = sollar_sys.update()
    return jsonify({"result": pos})

@app.route('/clear_all', methods=['POST'])
def clear():
    global sollar_sys
    data = request.get_json().get('variable')
    sollar_sys = sollar_system(1)
    return jsonify({"message": "Update"})
