@app.route('/your-endpoint', methods=['POST'])
def handle_variable():
    data = request.get_json()
    received_variable = float(data.get('variable'))
    if received_variable > 0.5:
        sollar_sys.integration(2 * math.pi / 361.156363004)
    elif received_variable < 0.5:
        sollar_sys.integration(-2 * math.pi / 361.156363004)
    else:
        sollar_sys.integration(0)
    print("Получено на сервере:", received_variable)
    # Делайте что-то с полученным значением
    return jsonify({"message": "Значение успешно получено на сервере"})
